import Map from 'pigeon-maps';
import Marker from 'pigeon-marker';
import ISSLocationContext from '../context/ISSLocationContext';
import { useContext } from 'react';
import { useEffect } from 'react';

function ISSLocation() {

  const { fetchISSLocation, fetchPeapleInSpace, latitude, longitude } = useContext(ISSLocationContext);

  useEffect(() => {
    const timer =  setInterval(() => (fetchISSLocation()), 1000);

    return () => {
      clearInterval(timer);
    }
  }, []);

  useEffect(() => {
    const peopleInSpaceTimer =  setInterval(() => (fetchPeapleInSpace()), 3000);

    return () => {
      clearInterval(timer);
    }
  }, []);

    return (
      <div className="map">
        <Map
          center={[0, 0]}
          defaultWidth={450}
          height={450}
          minZoom={1.5}
          maxZoom={8}
          zoom={1.5}
        >
          <Marker anchor={[latitude, longitude]} />
        </Map>
      </div>
      )
  }

export default ISSLocation;
