import React, { Component } from 'react';
import Map from 'pigeon-maps';
import Marker from 'pigeon-marker';
import { connect } from 'react-redux';
import { fetchISSLocation } from '../store/actions';

class ISSLocation extends Component {

  componentDidMount() {
    const { dispatch } = this.props;
    this.timer = setInterval(() => dispatch(fetchISSLocation()), 2000);
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  render() {
    const { latitude, longitude } = this.props;

    return (
      <div className="map">
        <Map
          center={[0, 0]}
          defaultWidth={450}
          height={450}
          minZoom={1.5}
          maxZoom={8}
          zoom={1.5}
        >
          <Marker anchor={[latitude, longitude]} />
        </Map>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    latitude: state.issLocationReducer.latitude,
    longitude: state.issLocationReducer.longitude
  }
}

ISSLocation.contextType = this

export default connect(mapStateToProps)(ISSLocation);
