import React, { Component } from 'react';
import { connect } from 'react-redux';
import ISSLocationContext from '../context/ISSLocationContext';

class Header extends Component {
  render() {
    const { latitude, longitude, loading, error } = this.props;

    return (
      <header>
        <h1>
          Space Station Tracker {loading && "⌛️" }
        </h1>
        <div>
          <p><strong>Latitude:</strong> { latitude }</p>
          <p><strong>Longitude:</strong> { longitude }</p>
          {error && <p>Erro: {error}</p>}
        </div>
      </header>
    );
  }
}

Header.contextType = ISSLocationContext;

const mapStateToProps = (state) => ({
  latitude: state.issLocationReducer.latitude,
  longitude: state.issLocationReducer.longitude,
  loading: state.issLocationReducer.isFetching,
  error: state.issLocationReducer.error,
});

export default connect(mapStateToProps)(Header);
