import './App.css';
import ISSLocation from './components/ISSLocation';
import Header from './components/Header';
import { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
  });
  const handleChange = ({target: {value, name}}) => (
    setFormData({
      [name]: value,
    })
  )
  return (
      <div className="App">
        <Header />
        <ISSLocation />
        <p>Nome: {formData.firstName}</p>
        <p>Sobrenome: {}</p>    
        <input value={formData.firstName} name="firstName" onChange={handleChange} type="text" />
        <input value={formData.lastName} name="lastName" onChange={handleChange} type="text" />
      </div>
  );
}

export default App;
