import { RECEIVE_ISS_LOCATION_SUCCESS, REQUEST_ISS_LOCATION, RECEIVE_ISS_LOCATION_FAILURE } from "../actions";

const INITIAL_STATE = {
  latitude: 0,
  longitude: 0,
  isFetching: false,
  error: '',
};

const issLocationReducer = (state = INITIAL_STATE, action) => {
  console.log("Action:", action);
  switch(action.type) {
    case REQUEST_ISS_LOCATION: return {
      ...state,
      isFetching: true
    }
    case RECEIVE_ISS_LOCATION_SUCCESS: return {
      ...state,
      latitude: action.latitude,
      longitude: action.longitude,
      isFetching: false
    }
    case RECEIVE_ISS_LOCATION_FAILURE: return {
      ...state,
      error: action.error,
      isFetching: false,
    }
    default: return state
  }
}

export default issLocationReducer;
